package com.amit.quotesdaily.adapters;import android.content.Context;import android.support.annotation.NonNull;import android.support.v4.view.PagerAdapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;/** * Created by Amit on 30-Mar-19. */public class IntroPagerAdapter extends PagerAdapter {    private LayoutInflater layoutInflater;    private Context context;    private int[] layouts;    public IntroPagerAdapter(Context context, int[] layouts) {        this.context = context;        this.layouts = layouts;    }    @NonNull    @Override    public Object instantiateItem(@NonNull ViewGroup container, int position) {        layoutInflater = (LayoutInflater) context.getSystemService(Context.LAYOUT_INFLATER_SERVICE);        View view = layoutInflater.inflate(layouts[position], container, false);        container.addView(view);        return view;    }    @Override    public int getCount() {        return layouts.length;    }    @Override    public boolean isViewFromObject(@NonNull View view, @NonNull Object obj) {        return view == obj;    }    @Override    public void destroyItem(@NonNull ViewGroup container, int position, @NonNull Object object) {        View view = (View) object;        container.removeView(view);    }}